  CREATE OR REPLACE TRIGGER "DODREAM"."TRG_HIS_INSERT" 
AFTER INSERT
ON HISTORY
FOR EACH ROW
BEGIN
    UPDATE REWARD
    SET REWARD_AMOUNT = REWARD_AMOUNT - :NEW.REWARD_SUM
    WHERE REWARD_NO = :NEW.REWARD_NO;
END;

