--  DDL for Trigger TRG_RSV_UPDATE
--------------------------------------------------------

  CREATE OR REPLACE TRIGGER "DODREAM"."TRG_RSV_UPDATE" 
AFTER UPDATE
ON RESERVE
FOR EACH ROW
DECLARE
    V_RWD_NO HISTORY.REWARD_NO%TYPE;
    V_RWD_SUM HISTORY.REWARD_SUM%TYPE;
    V_COUNT NUMBER;

    -- 커서 생성 선언
    CURSOR C1
    IS SELECT H.REWARD_NO, H.REWARD_SUM 
        FROM HISTORY H
        WHERE RESERVE_NO = :NEW.RESERVE_NO;
BEGIN
    SELECT COUNT(*)
    INTO V_COUNT
    FROM HISTORY
    WHERE RESERVE_NO = :NEW.RESERVE_NO;

    DBMS_OUTPUT.PUT_LINE('트리거 실행');
    DBMS_OUTPUT.PUT_LINE(:NEW.RESERVE_NO);

