AFTER UPDATE ON RESERVE
FOR EACH ROW
DECLARE
    V_PROJECT_NO NUMBER;
    V_REWARDPRICE NUMBER;
    V_REWARDSUM NUMBER;
    V_ADDI NUMBER;
    V_RESERVE_NO NUMBER;
    V_RESERVE_STATUS_NO NUMBER;
    V_COUNT NUMBER;
        -- 커서 생성 선언
