    IS SELECT H.REWARD_SUM, R.REWARD_PRICE 
        FROM HISTORY H 
        JOIN REWARD R ON(H.REWARD_NO = R.REWARD_NO)
        WHERE RESERVE_NO = :NEW.RESERVE_NO;

BEGIN
    SELECT COUNT(*)
    INTO V_COUNT
    FROM HISTORY
    WHERE RESERVE_NO = :NEW.RESERVE_NO;

    DBMS_OUTPUT.PUT_LINE('트리거 실행');
    DBMS_OUTPUT.PUT_LINE(:NEW.RESERVE_NO);

    IF :NEW.RESERVE_STATUS_NO = 3
    THEN
        DBMS_OUTPUT.PUT_LINE('빼기 동작 실행');
        OPEN C1; -- 커서 오픈

        LOOP
            FETCH C1 --커서 패치 : 서브쿼리의 결과에서 한 ROW씩 순서대로 데이터를 가져옴
            INTO V_REWARDSUM, V_REWARDPRICE;

            DBMS_OUTPUT.PUT_LINE(C1%ROWCOUNT);

            UPDATE PROJECT_TB
            SET PROJECT_TOTAL = PROJECT_TOTAL - (V_REWARDSUM * V_REWARDPRICE)
            WHERE PROJECT_NO = :NEW.RESERVE_REF_PNO;

            EXIT WHEN V_COUNT = C1%ROWCOUNT;
        END LOOP;
        CLOSE C1;

    END IF;

    UPDATE PROJECT_TB
    SET PROJECT_TOTAL = PROJECT_TOTAL - :OLD.ADDITIONAL
    WHERE PROJECT_NO = :NEW.RESERVE_REF_PNO;

END;

